from enum import Enum
from collections import namedtuple
from itertools import product
from typing import cast, Iterable

raw_data = """





"""

class Direction(Enum):
    D = (1, 0)
    R = (0, 1)
    U = (-1, 0)
    L = (0, -1)


# Point = Iterable[int]
Point = namedtuple("Point", ["x", "y"])
Keypad = dict[Point, str]
Instructions = list[Direction]

data = [[Direction[instruction] for instruction in row] for row in raw_data.split()]
keypad_a = {
    Point(*point): str(i) for i, point in enumerate(product(range(3), repeat=2), start=1)
}
keypad_b = {
    Point(*point): i
    for i, point in zip(
        "123456789ABCD",
        filter(lambda x: (abs(x[0]) + abs(x[1])) <= 2, product(range(-2, 3), repeat=2)),
    )
}

def follow_instrctions(instructions: Instructions, current_button: Point, keypad: Keypad) -> Point:
    for instruction in instructions:
        new_button = Point(*(i + j for i, j in zip(current_button, instruction.value)))

        if keypad.get(new_button):
            current_button = new_button
    return current_button


def solve(data: list[Instructions], current_button: Point, keypad: Keypad) -> str:
    result = []

    for instructions in data:
        current_button = follow_instrctions(instructions, current_button, keypad)
        result.append(keypad[current_button])

    return "".join(result)


current_button = Point(1, 1)
sol_a = solve(data, current_button, keypad_a)
print(f"{sol_a = }")

current_button = Point(0, -2)
sol_b = solve(data, current_button, keypad_b)
print(f"{sol_b = }")
